const sortNumbers = (a, b) => a - b
const primeNumber = (x) => {
    let count = 0
    for (let i = 1; i <= x; i++) {
        if (x % i === 0) {
            count++
        }
    }
    if (count == 2) {
        console.log(true)
    } else {
        console.log(false)
    }
}

module.exports.sortNumbers = sortNumbers
module.exports.primeNumber = primeNumber